const filterReducer = (state = '', action) => {
    console.log('action', action)

    switch(action.type) {
        case 'FILTER_SEARCH':
            const filter = action.payload
            console.log(filter)
            return state.concat(filter)
        default:
            return state
    }
}

export const applyThisFilter = (content) => {
    return{
        type: 'FILTER_SEARCH',
        payload: content
    }
}

export default filterReducer
