import { useSelector, useDispatch } from "react-redux";
import { voteOf } from '../reducers/anecdoteReducer'
import { changeNotification, hideNotification } from "../reducers/notificationReducer";

const AnecdoteList = () => {
    const dispatch = useDispatch()
    const byVotes = (b1, b2) => b2.votes - b1.votes
    const anecdotes = useSelector(({filterSearch, anecdotes}) => {
      if (filterSearch !== '') {
        const regex = new RegExp( filterSearch, 'i' )
        return anecdotes.filter(anecdote => anecdote.content.match(regex))
      }
      return anecdotes
    })

    const sleep = async (milliseconds) => {
      await new Promise(resolve => {
          return setTimeout(resolve, milliseconds)
      })
    }

    const vote = (id) => {
      dispatch(voteOf(id))
      const anecdoteToVote = anecdotes.find(anecdote => anecdote.id === id)
      dispatch(changeNotification(`Voted for "${anecdoteToVote.content}"`))
      sleep(5000)
      dispatch(hideNotification())
    }

    return (
        <div>
          {[...anecdotes].sort(byVotes).map(anecdote =>
            <div key={anecdote.id}>
              <div>
                {anecdote.content}
              </div>
              <div>
                has {anecdote.votes}
                <button onClick={() => vote(anecdote.id)}>vote</button>
              </div>
            </div>
          )}
        </div>
      )
}

export default AnecdoteList
