import { useReducer } from 'react'

const notifReducer = (state, action) => {
  switch (action.type) {
    case "CHANGE":
        return action.payload
    case "HIDE":
        return ''
    default:
        return state
  }
}

const Notification = () => {
  const [notif, notifDispatch] = useReducer(notifReducer, '')

  let style = {}
  
  if (notif) {
    style = {
      border: 'solid',
      padding: 10,
      borderWidth: 1,
      marginBottom: 5
    }
  }

  return (
    <div style={style}>
      {notif}
    </div>
  )
}

export default Notification
